mapper.insert:
insert into m_user(
	user_id
	, password
	, user_name
	, birthday
	, age
	, gender
	, department_id
	, role
)
values (
	#{userId}
	, #{password}
	, #{userName}
	, #{birthday}
	, #{age}
	, #{gender}
	, #{departmentId}
	, #{role}
)

mapper.update:
update
	m_user
set
	password = #{password}
    , user_name = #{userName}
where
	user_id = #{userId}
